// 工具方法
export default class commonUtils {
  // 获取当前浏览器的语种
  static getLanguage(): string {
    let language: string | null = localStorage.getItem('LANGUAGE')
    if (language) return language

    switch (window.navigator.language) {
      case 'zh-CN':
        language = 'CHN'
        break
      case 'zh-TW':
      case 'zh-HK':
        language = 'THN'
        break
    }
    if (language) return language

    switch (window.navigator.language.substr(0, 2)) {
      case 'zh':
        language = 'CHN'
        break
      case 'en':
        language = 'ENG'
        break
    }
    return language ?? 'CHN'
  }

  // 左侧添加内容
  static padLeft(str: string, length: number, char: string): string {
    if (!str || str.length >= length) return str
    const arr: string[] = []
    for (let i = 0; i < length - str.length; i++) {
      arr.push(char)
    }
    arr.push(str)
    return arr.join('')
  }

  // 格式化时间戳
  static formatDate(time: number | Date, format?: string): string {
    if (!time) return ''
    format ??= 'yyyy-MM-dd HH:mm:ss'
    let date: Date = new Date()
    if (time instanceof Date) {
      date = time
    } else if (typeof time === 'number') {
      date = new Date(time)
    }
    const ymd: string[] = [
      date.getFullYear().toString(),
      (date.getMonth() + 1).toString(),
      date.getDate().toString()
    ],
      hms: string[] = [
        date.getHours().toString(),
        date.getMinutes().toString(),
        date.getSeconds().toString()
      ]

    if (!date.getDate()) {
      console.error('Invalid Msec for "util.toDateString(Msec)"')
      return time.toString()
    }

    format = format || 'yyyy-MM-dd HH:mm:ss'
    return format
      .replace(/yyyy/g, ymd[0])
      .replace(/MM/g, this.padLeft(ymd[1], 2, '0'))
      .replace(/dd/g, this.padLeft(ymd[2], 2, '0'))
      .replace(/HH/g, this.padLeft(hms[0], 2, '0'))
      .replace(/mm/g, this.padLeft(hms[1], 2, '0'))
      .replace(/ss/g, this.padLeft(hms[2], 2, '0'))
  }

  // 秒格式化成为 HH:ss
  static formatSecond(second: number): string {
    if (!second) return '00:00'
    let minute = Math.floor(second / 60)
    second %= 60
    minute = Math.max(0, minute)
    second = Math.max(0, second)
    return `${this.padLeft(minute.toString(), 2, '0')}:${this.padLeft(second.toString(), 2, '0')}`
  }

  // 天数累加
  static addDays(days: number, date?: Date | number): Date {
    if (!date) date = new Date()
    if (typeof date === 'number') date = new Date(date);
    return new Date(date.getTime() + days * 24 * 3600 * 1000)
  }

  // 异步方法中的等待
  static async wait(timeout: number): Promise<void> {
    return new Promise((resolve) => {
      setTimeout(resolve, timeout)
    })
  }

  // 获取总数
  static sum(list: any[], field: Function): number {
    let sum: number = 0
    list.forEach((item: any) => {
      sum += field(item)
    })
    return sum
  }

  // 获取当前的运行环境
  static getPlatform(): platform {
    return new platform()
  }

  // IOS下的safari添加到主屏幕的交互
  static addSecreen(): void {
    //#region
    const image =
      'data:image/png;base64,'
    //#endregion
    const plarform = this.getPlatform()
    if (plarform.IOS && !plarform.APP && plarform.browser === 'Safari') {
      const elem = window.document.createElement('div')
      elem.setAttribute('id', 'safari-addSecreen')
      elem.setAttribute(
        'style',
        `background: url(${image});
            background-size: 100% 100%;
            border-radius: 0.4rem;
            box-shadow: 0 0.08rem 0.08rem rgba(0,0,0,.25);
            position: fixed;
            left: 50%;
            -webkit-transform: translateX(-50%);
            transform: translateX(-50%);
            z-index: 1000;
            transition: all .5s ease;
            width: 6.7rem;
            height: 6.86rem;`
      )
      elem.innerHTML = '<div>点击“添加到主屏幕”按钮<br /></div>'
      window.document.body.appendChild(elem)
    }
  }

  // 获取最大页码
  static getMaxPage(recordCount: number, pageSize: number): number {
    if (recordCount === 0 || pageSize === 0) return 0
    if (recordCount % pageSize === 0) return recordCount / pageSize
    return Math.floor(recordCount / pageSize) + 1
  }

  // 金额转中文大写
  static uppercase(n: number): string {
    const fraction = ['角', '分'],
      digit = ['零', '壹', '贰', '叁', '肆', '伍', '陆', '柒', '捌', '玖'],
      unit = [
        ['元', '万', '亿'],
        ['', '拾', '佰', '仟']
      ],
      head = n < 0 ? '欠' : ''
    n = Math.abs(n)
    let s = ''
    for (let i = 0; i < fraction.length; i++) {
      s += (digit[Math.floor(n * 10 * Math.pow(10, i)) % 10] + fraction[i]).replace(/零./, '')
    }
    s = s || '整'
    n = Math.floor(n)
    for (let i = 0; i < unit[0].length && n > 0; i++) {
      let p = ''
      for (let j = 0; j < unit[1].length && n > 0; j++) {
        p = digit[n % 10] + unit[1][j] + p
        n = Math.floor(n / 10)
      }
      s = p.replace(/(零.)*零$/, '').replace(/^$/, '零') + unit[0][i] + s
    }
    return (
      head +
      s
        .replace(/(零.)*零元/, '元')
        .replace(/(零.)+/g, '零')
        .replace(/^整$/, '零元整')
    )
  }

  static percent(num: number, digi?: number): string {
    if (!digi) digi = 2
    return (num * 100).toFixed(2) + '%'
  }

  // 保存页面缓存
  static savePageCache(data: any): void {
    const hashName: string = window.location.hash
    sessionStorage.setItem(`PAGE:CACHE${hashName}`, JSON.stringify(data))
  }

  static getPageCache(): any {
    const hashName: string = window.location.hash
    const store: string | null = sessionStorage.getItem(`PAGE:CACHE${hashName}`)
    if (!store) return null
    try {
      return JSON.parse(store)
    } catch {
      this.removePageCache()
      return null
    }
  }

  static removePageCache(): void {
    Object.keys(sessionStorage).forEach((key: string) => {
      if (/^PAGE:CACHE/.test(key)) sessionStorage.removeItem(key)
    })
  }

  static getNumberClass(num: number | undefined): string {
    if (!num) return "zero";
    if (num > 0) return "positive";
    return "negative";
  }
}

class platform {
  // 操作系统
  OS: string

  // 浏览器类型
  browser: string

  // 浏览器版本
  version: string

  Android: boolean = false

  IOS: boolean = false

  // 是否是在封装的APP中
  APP: boolean = false

  constructor() {
    this.OS = 'IOS'
    this.browser = 'Safari'
    this.version = '1.0'
    this.IOS = true
  }

  toString(): string {
    return JSON.stringify(this)
  }
}
